import React, { useState } from 'react';

function Square({ value, onClick }) {
  return (
    <button className="square" onClick={onClick}>
      {value}
    </button>
  );
}

function Board({ squares, onClick }) {
  const renderSquare = (i) => (
    <Square value={squares[i]} onClick={() => onClick(i)} />
  );

  return (
    <div>
      <div className="board-row">
        {renderSquare(0)}
        {renderSquare(1)}
        {renderSquare(2)}
        {renderSquare(3)}
        {renderSquare(4)}
        {renderSquare(5)}
        {renderSquare(6)}
        {renderSquare(7)}
        {renderSquare(8)}
        {renderSquare(9)}
        {renderSquare(10)}
        {renderSquare(11)}
        {renderSquare(12)}
        {renderSquare(13)}
        {renderSquare(14)}
      </div>
      {/* 重复上述代码以创建15行 */}
    </div>
  );
}

export default function Game() {
  const initialSquares = Array(15 * 15).fill(null);
  const [squares, setSquares] = useState(initialSquares);
  const [xIsNext, setXIsNext] = useState(true);

  const handleClick = (i) => {
    const nextSquares = squares.slice();
    if (calculateWinner(nextSquares, i) || squares[i]) {
      return;
    }
    nextSquares[i] = xIsNext ? 'B' : 'W';
    setSquares(nextSquares);
    setXIsNext(!xIsNext);
  };

  const winner = calculateWinner(squares);

  let status;
  if (winner) {
    status = 'Winner: ' + (winner === 'B' ? 'Black' : 'White');
  } else {
    status = 'Next player: ' + (xIsNext ? 'Black' : 'White');
  }

  return (
    <div className="game">
      <div className="game-board">
        <Board squares={squares} onClick={handleClick} />
      </div>
      <div className="game-info">
        <div>{status}</div>
        <button onClick={() => setSquares(initialSquares)}>Restart</button>
      </div>
    </div>
  );
}

